/**
 * The form for creating a new form
 */
export class NewForm extends React.PureComponent {
	/**
	 * Create NewForm component
	 * @param {Object} props
	 */
	constructor(props){
		super(props);
		this.state = {
			newFormName: '',
			newFormTemplate: '',
			newFormCreateFrom: 'templates',
		};
		this.getFieldComponents = this.getFieldComponents.bind(this);
	}

	/**
	 * Create the field components for NewForm
	 */
	getFieldComponents(){
		let fields = {};
		this.props.fieldConfigs.forEach( fieldConfig => {
			const {id} = fieldConfig;
			if ( 'newFormSubmitButton' !== id ) {
				fieldConfig.onValueChange = (newValue) => {
					let update = {};
					update[id] = newValue;
					this.setState(update);
				};
				fieldConfig.value = this.state[id];
				if( 'newFormTemplate' === id ){
					fieldConfig.options = this.props.templates;
				}
			}else{
				fieldConfig.onClick = () => {
					this.props.onCreate({
						...this.state
					});
				}
			}
			fields[id] = fieldFactory(fieldConfig);
		});
		return fields;

	}

	/**
	 * Render NewForm component
	 * @return {*}
	 */
	render(){
		const components = this.getFieldComponents();
		return (
			<div>
				{this.props.fieldConfigs.map(fieldConfig => {
					const {id} = fieldConfig;
					return React.createElement(
						React.Fragment,
						{
							key: `newForm-${id}`,
						},
						components[id]
					);
				})}
			</div>
		)
	}


};

/**
 * prop definitions for NewForm component.
 *
 * @type {{fieldConfigs: shim, onCreate: *, templates: shim}}
 */
NewForm.propTypes = {
	fieldConfigs: propTypes.array,
	onCreate: propTypes.func.isRequired,
	templates: propTypes.array
};

/**
 * Default props for NewForm component
 *
 * @type {{fieldConfigs: *[]}}
 */
NewForm.defaultProps = {
	fieldConfigs
};

import PropTypes from 'prop-types';
import configFields, {CDN_ENABLE, STYLE_ALERT, STYLE_FORM, STYLE_GRID} from './configFields'
import {object, pick} from "dot-object";
import {SettingsGroup} from "../SettingsGroup";

/**
 * Default values for style includes settings
 * @type {{[p: string]: boolean}}
 */
const styleIncludesDefaults = {

	[STYLE_FORM]: true,
	[STYLE_ALERT]: true,
	[STYLE_GRID]: true,

};

/**
 * Default values for other settings
 * @type {{[p: string]: boolean}}
 */
const otherSettingsDefaults = {
	[CDN_ENABLE]: false
};

/**
 * Create the global form settings UI
 * @param {Object} props
 * @return {*}
 * @constructor
 */
export class GlobalFormSettings extends SettingsGroup {
	constructor(props) {
		super(props);
		this.state = {
			styleIncludes: {
				...styleIncludesDefaults,
				...props.styleIncludes
			},
			otherSettings: {
				...otherSettingsDefaults,
				...props.otherSettings
			}
		}
	}

	/**
	 * Save the settings
	 */
	onSettingsSave() {
		this.props.onSettingsSave(
			this.state
		);
	}

	/**
	 * Update internal state when settings change
	 *
	 * @param {Object} update
	 */
	onSettingsChange(update) {
		this.setState(update);
	}


	/**
	 * Prepare config fields
	 *
	 * @return {Array}
	 */
	getConfigFields() {
		let currentConfigFields = this.props.configFields;
		currentConfigFields.forEach(configField => {
			const {path} = configField;
			configField.value = pick(
				path,
				this.state
			);

			configField.onValueChange = (newValue) => {
				const update = {
					...this.state,
					[path]: newValue
				};
				this.onSettingsChange(object(update));
			};

		});
		return currentConfigFields;
	};
};

/**
 * Prop types for the GlobalForms settings component
 * @type {{}}
 */
GlobalFormSettings.propTypes = {
	classNames: PropTypes.string,
	onSettingsSave: PropTypes.func.isRequired,
	styleIncludes: PropTypes.object,
	otherSettings: PropTypes.object
};
